       private static string GetContent(string src)
        {
            var web = new HtmlWeb();
            string temp = src.Replace(" ", "+");
            var doc = web.Load("http://www.imdb.com/find?ref_=nv_sr_fn&q=" + temp + "&s=all");

            HtmlNode resultNode = doc.DocumentNode.SelectSingleNode("//table[@class = 'findList']");
            if (resultNode != null)
            {
                var trNode = doc.DocumentNode.SelectSingleNode("//tr[@class = 'findResult']");
                var tdNode = doc.DocumentNode.SelectSingleNode("//td[@class = 'result_text']");
                var aNode = doc.DocumentNode.SelectSingleNode("//a[@href]");
                //return tdNode.InnerText;
                return aNode.Attributes["href"].Value;

            }
            else
            {
                return "Nothing found";
            }
        }
